import React, { useState } from "react";

// 1) filll form
// 2) send to server
// 3) if valid data - redicert to home page, and save user in local storage
// if not valid data - show error message

function Login(props) {
   console.log("login props", props);

   const [form, setForm] = useState({ email: "", password: "" });
   function onSubmit(event) {
      // prevet default form behavior
      event.preventDefault();
      const email = event.target.email.value;
      const password = event.target.password.value;

      // validate data in server (Fetch)
      fetch("http://localhost:5000/tasks/", {
         method: "POST",
         headers: {
            "Content-Type": "application/json", // body type
         },
         body: JSON.stringify({
            userEmail: "my@mail.com",
            taskTitle: taskInput.task,
            taskCategory: taskInput.category,
            clientName: taskInput.client,
            duration: timer.seconds * 1000 + timer.minutes * 60000 + timer.hours * 3600000,
         }),
      })
         .then(function (response) {
            // run after get response from server
            console.log("res", response);
            return response.json(); // wait to body data from server (PROMISE)
         })
         .then(function (body) {
            //   run after all body back from server
            console.log(body);
         });
      // if success
      const data = { name: "Mosssshe" };

      // save user form server in local storage
      localStorage.setItem("user", JSON.stringify(data));

      // redirect to home
      props.history.push("/");
   }

   function onChange(event) {
      {
         const { name, value } = event.target;
         setForm({ ...form, [name]: value });
      }
   }

   console.log("form", form);

   return (
      <div>
         <form onSubmit={onSubmit}>
            email: <input value={form.email} name='email' onChange={onChange} /> <br />
            password <input type='password' name='password' value={form.password} onChange={onChange} /> <br />
            <button type='sumbit'>sumbit</button>
         </form>
      </div>
   );
}

export default Login;
