import React, { useState } from "react";

function Stopwatch() {
  const [timer, setTimer] = useState({
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  const [intervalId, setIntervalId] = useState(null);

  function watchDone() {
    clearInterval(intervalId);
  }

  function watchToggle() {
    if (!intervalId) {
      // start timer
      const id = setInterval(() => {
        setTimer((prevState) => {
          let minutes = prevState.minutes;
          let seconds = prevState.seconds;
          let hours = prevState.hours;

          if (seconds === 59) {
            seconds = 0;
            minutes++;
          } else {
            seconds++;
          }

          if (minutes === 59) {
            minutes = 0;
            hours++;
          }
          return { seconds, minutes, hours };
        });
      }, 10);

      //  save interval id
      setIntervalId(id);
    } else {
      // pause timer
      clearInterval(intervalId);
      //   remove internal id from stae
      setIntervalId(null);
    }
  }

  return (
    <div>
      <h1>
        {timer.hours}:{timer.minutes}:{timer.seconds}
      </h1>
      <button onClick={watchToggle}>{intervalId ? "pause" : "start"}</button>
      <button onClick={watchDone}>Done</button>
      <button>Reset</button>
    </div>
  );
}

export default Stopwatch;
