import React, { useState, useEffect } from "react";

import { Redirect } from "react-router-dom";
import TaskTable from "./TaskTable";

function Stopwatch() {
   // set initial state for stopwatch
   const user = JSON.parse(localStorage.getItem("loggedinUser"));
   console.log("user", user);

   const [timer, setTimer] = useState({
      hours: 0,
      minutes: 0,
      seconds: 0,
   });
   // set the intervalid to null
   const [intervalId, setIntervalId] = useState(null);

   const [startButton, setStartButton] = useState("Start");
   // set input fields to empty
   const [taskInput, setTaskInput] = useState({
      task: "",
      category: "",
      client: "",
   });

   const [tasksArray, setTasksArray] = useState([]);

   const [message, setMessage] = useState({
      showMessage: false,
      messageSuccess: false,
   });

   // Sets the states based on user inputs
   function handleInput(event) {
      const value = event.target.value;
      const fieldName = event.target.name;
      setTaskInput((prevState) => {
         return {
            ...prevState,
            [fieldName]: value,
         };
      });
   }

   function watchToggle() {
      messageClear();
      if (!intervalId) {
         // start timer
         setStartButton("Pause");
         const id = setInterval(() => {
            setTimer((prevState) => {
               let minutes = prevState.minutes;
               let seconds = prevState.seconds;
               let hours = prevState.hours;

               if (seconds === 59) {
                  seconds = 0;
                  minutes++;
               } else {
                  seconds++;
               }

               if (minutes === 59) {
                  minutes = 0;
                  hours++;
               }
               return { seconds, minutes, hours };
            });
         }, 1000);

         //  save interval id
         setIntervalId(id);
      } else {
         // pause timer
         clearInterval(intervalId);
         setStartButton("Resume");
         //   remove internal id from stae
         setIntervalId(null);
      }
   }

   function watchDone() {
      clearInterval(intervalId);
      setIntervalId(null);
      setStartButton("Start");
      // send request to server
      fetch("http://localhost:5000/tasks/", {
         method: "POST",
         headers: {
            "Content-Type": "application/json", // body type
         },
         body: JSON.stringify({
            userEmail: user,
            taskTitle: taskInput.task,
            taskCategory: taskInput.category,
            clientName: taskInput.client,
            duration: timer.seconds * 1000 + timer.minutes * 60000 + timer.hours * 3600000,
         }),
      })
         .then(function (response) {
            // run after get response from server
            console.log("res", response);
            return response.json(); // wait to body data from server (PROMISE)
         })
         .then(function (body) {
            //   run after all body back from server
            console.log(body);

            setMessage({
               showMessage: true,
               messageSuccess: body.success,
            });
         })
         .then(
            setTaskInput({
               task: "",
               category: "",
               client: "",
            }),
         )
         .then(
            setTimer({
               hours: 0,
               minutes: 0,
               seconds: 0,
            }),
         );
   }

   function watchReset() {
      clearInterval(intervalId);
      setIntervalId(null);
      setStartButton("Start");
      setTimer({
         hours: 0,
         minutes: 0,
         seconds: 0,
      });
      setTaskInput({
         task: "",
         category: "",
         client: "",
      });
   }

   function messageClear() {
      setMessage({
         showMessage: false,
         messageSuccess: false,
      });
   }

   // run code after every render
   useEffect(() => {
      function getUserTasks() {
         fetch("http://localhost:5000/tasks/" + user)
            .then((response) => response.json())
            .then((data) => setTasksArray(data));
      }

      getUserTasks();
   }, []);

   console.log("tasksArray", tasksArray);

   if (!user) {
      // if no user in local storage redirect to login
      return <Redirect to='/login' />;
   }

   return (
      <div>
         hi {user.substring(0, user.lastIndexOf("@"))}
         <div className='digits-container'>
            <div className='digits'>
               {("0" + timer.hours.toString()).slice(-2)}:{("0" + timer.minutes.toString()).slice(-2)}:
               {("0" + timer.seconds.toString()).slice(-2)}
            </div>
         </div>
         <div className='button-container'>
            <button
               className='start-button'
               disabled={!taskInput.task || !taskInput.category || !taskInput.client}
               onClick={watchToggle}>
               {startButton}
            </button>
            <button
               className='done-button'
               disabled={
                  !taskInput.task ||
                  !taskInput.category ||
                  !taskInput.client ||
                  (timer.seconds === 0 && timer.minutes === 0 && timer.hours === 0)
               }
               onClick={watchDone}>
               Done
            </button>
            <button onClick={watchReset} className='reset-button'>
               Discard
            </button>
         </div>
         {message.showMessage && (
            <div className='alert-container'>
               <div className={`alert-${message.messageSuccess ? "success" : "failure"}`}>
                  <span className='closebtn' onClick={messageClear}>
                     &times;
                  </span>
                  {message.messageSuccess ? "Success!" : " Failure!"}
               </div>
            </div>
         )}
         <div className='inputs-container'>
            <input onChange={handleInput} name='task' placeholder='Enter task' value={taskInput.task}></input>
            <input
               onChange={handleInput}
               type='text'
               name='category'
               list='categoryList'
               placeholder='Category'
               value={taskInput.category}
            />
            <datalist id='categoryList'>
               <option value=' ' />
               <option value='Recording' />
               <option value='Mixing' />
               <option value='Video Editing' />
               <option value='Programming' />
               <option value='Whatever' />
            </datalist>
            <input onChange={handleInput} name='client' placeholder='Enter Client' value={taskInput.client}></input>
         </div>
         <div className='task-container'>!!!!!</div>
      </div>
   );
}

export default Stopwatch;
